var mongoose = require('mongoose');
var models = require('config/models')
var category = models.category;

exports.findcategory = function(req,res,callback) {

	category.find(function(err,result){
		if(err){
			var status = {
				status: false
			}
		}else{
			var status = {
				status: true,
				result: result
			}
		}
		res.json(status);
		console.log("categories returned");
	})
}
