var mongoose = require('mongoose');

var Schema = mongoose.Schema;

var userSchema = mongoose.Schema({
	token : String,
	email: String,
	hashed_password: String,
	salt : String,
}, { collection: 'users' });

var invent = mongoose.Schema({
	id : Number,
	name : String,
	category : String,
	price : Number,
	cid : Number
}, { collection: 'inventory' });

var category = mongoose.Schema({
    cid : Number,
    type : String
}, { collection: 'category'});

mongoose.connect('mongodb://localhost:27017/node-android');
module.exports.users = mongoose.model('users', userSchema);
module.exports.inventory = mongoose.model('inventory', invent);
module.exports.category = mongoose.model('category', category);
