var mongoose = require('mongoose');
var models = require('config/models')
var inventory = models.inventory;

exports.findproduct = function(req,res,callback) {

	inventory.find(function(err,result){
		if(err){
			var status = {
				status: false
			}
		}else{
			var status = {
				status: true,
				result: result
			}
		}
		res.json(status);
		console.log("products returned");
	})
}
